import { Component } from "@angular/core";
import { SmoothieChart, TimeSeries } from "smoothie";

@Component({
  selector: "app-analytics",
  templateUrl: "./analytics.component.html",
  styleUrls: ["./analytics.component.scss"],
})
export class AnalyticsComponent implements OnInit {
  series: TimeSeries = new TimeSeries();

  canvas = document.getElementById("chart");

  chart = new SmoothieChart();
  ngOnInit(): void {
    const params = this.activatedRoute.snapshot.params;
    this.searchFormGroup = this.formBuilder.group({
      keyword: this.formBuilder.control(""),
    });
    if (params["id"]) {
      this.handleGetCustomerPageBill(
        Number(params["id"]),
        this.currentPage,
        this.pageSize
      );
    } else {
      this.handleGetPageBill(this.currentPage, this.pageSize);
    }
  }
  chart.addTimeSeries(series, { strokeStyle: 'rgba(0, 255, 0, 1)' });
  chart.streamTo(canvas, 500);
}
<div class="container">
  <div class="row">
    <div class="col-6 bg-success">Hello</div>
    <div class="col-6 bg-success">Everyone</div>
  </div>
  <div class="row">
    <canvas id="chart" class="col-8 mx-auto"></canvas>
  </div>
</div>
