import { ProductItem } from "./../model/productItem.model";
import { Customer, PageCustomer } from "./../model/customer.model";
import { CustomerService } from "./../services/customer.service";
import { ProductService } from "./../services/product.service";
import { PageProduct, Product } from "./../model/product.model";
import { Bill } from "./../model/bill.model";
import { BillService } from "./../services/bill.service";
import {
  FormGroup,
  FormBuilder,
  Validators,
  ValidationErrors,
} from "@angular/forms";
import { Component, OnInit } from "@angular/core";

@Component({
  selector: "app-new-bill",
  templateUrl: "./new-bill.component.html",
  styleUrls: ["./new-bill.component.scss"],
})
export class NewBillComponent implements OnInit {
  billFormGroup!: FormGroup;
  message: string = "";
  pageProduct!: PageProduct;
  pageCustomer!: PageCustomer;
  selectedProducts: Array<ProductItem> = [];
  selectedCustomer: number = -1;

  constructor(
    private fb: FormBuilder,
    private billService: BillService,
    private productService: ProductService,
    private customerService: CustomerService
  ) {}
  ngOnInit(): void {
    this.billFormGroup = this.fb.group({
      billingDate: this.fb.control(new Date(), [Validators.required]),
    });
    this.productService.getPageProductsByKeyword("", 0, 100).subscribe({
      next: (data) => {
        this.pageProduct = data;
      },
      error: (err) => alert(err.error),
    });
    this.customerService.getPageCustomersByKeyword("", 0, 100).subscribe({
      next: (data) => {
        this.pageCustomer = data;
      },
      error: (err) => alert(err.error),
    });
  }
  getErrorMessage(name: string, error: ValidationErrors): string {
    if (error["required"]) return `${name}  is required`;
    if (error["minlength"])
      return `The minimum length of ${name} is ${error["minlength"]["requiredLength"]}`;
    if (error["min"])
      return `The min value of ${name} is ${error["min"]["min"]}`;
    return "";
  }
  handleNewBillFormSubmit() {
    const { billingDate, productItems, customerID } = this.billFormGroup.value;
    console.log(customerID, productItems, billingDate);
    // this.billService.addBill(id, name).subscribe({
    //   next: (bill) => {
    //     this.billFormGroup.reset();
    //     this.message = "Bill newed successfully";
    //   },
    //   error: (err) => console.log(err),
    // });
  }
  handleCloseAlert() {
    this.message = "";
  }
  setSelectedCustomer(customerID: number) {
    console.log(customerID);
    this.selectedCustomer = customerID;
  }
  addSelectedProduct(productID: number) {}
  removeSelectedProduct(productID: number) {}
  isSelectedProduct(productID: number) {
    return false;
  }
  isSelectedCustomer(customerID: number) {
    return this.selectedCustomer === customerID;
  }
}
<div class="container mt-3 col-md-6 offset-md-3">
  <div
    *ngIf="message.length > 0"
    class="alert alert-success alert-dismissible fade show"
    role="alert"
  >
    <button type="button" class="close" data-dismiss="alert" aria-label="Close">
      <span aria-hidden="true">&times;</span>
      <span class="sr-only" (click)="handleCloseAlert()">Close</span>
    </button>
    <strong>
      {{ message }}
    </strong>
  </div>
  <form
    *ngIf="billFormGroup"
    [formGroup]="billFormGroup"
    (ngSubmit)="handleNewBillFormSubmit()"
  >
    <div class="mb-3">
      <label class="form-label">Billing date</label>
      <input type="date" formControlName="billingDate" class="form-control" />
      <small
        class="text-danger"
        *ngIf="
          billFormGroup.controls['billingDate'].errors &&
          billFormGroup.controls['billingDate'].touched
        "
      >
        {{
          getErrorMessage(
            "Billing Date",
            billFormGroup.controls["billingDate"].errors
          )
        }}
      </small>
    </div>

    <div class="mb-3">
      <label class="form-label">Customer</label>
      <div class="container">
        <div class="row">
          <div
            class="card col-sm-12 col-md-6 col-lg-4"
            *ngFor="let customer of pageCustomer._embedded.customers"
          >
            <div class="card-body">
              <h5 class="card-title">{{ customer.name }}</h5>
              <p class="card-text">
                {{ customer.email }}
              </p>
              <button
                type="button"
                *ngIf="!isSelectedCustomer(customer.id)"
                class="btn btn-primary"
                (click)="setSelectedCustomer(customer.id)"
              >
                Select
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>

    <div class="mb-3">
      <label class="form-label">Products</label>
      <div class="container">
        <div class="row">
          <div
            class="card col-sm-12 col-md-6 col-lg-4"
            *ngFor="let product of pageProduct._embedded.products"
          >
            <div class="card-body">
              <h5 class="card-title">{{ product.name }}</h5>
              <p class="card-text">
                {{ product.price }}
              </p>
              <button
                *ngIf="isSelectedProduct(product.id)"
                type="button"
                class="btn btn-success"
                (click)="addSelectedProduct(product.id)"
              >
                Add
              </button>

              <button
                *ngIf="!isSelectedProduct(product.id)"
                type="button"
                class="btn btn-danger"
                (click)="removeSelectedProduct(product.id)"
              >
                Remove
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>

    <div class="mb-3">
      <button
        type="submit"
        class="btn btn-outline-warning"
        [disabled]="!billFormGroup.valid"
      >
        Add bill
      </button>
    </div>
  </form>
</div>
