import { Bill } from "./../model/bill.model";
import { BillService } from "./../services/bill.service";
import {
  FormGroup,
  FormBuilder,
  Validators,
  ValidationErrors,
} from "@angular/forms";
import { Component, OnInit } from "@angular/core";
import { ActivatedRoute, Route, Router } from "@angular/router";

@Component({
  selector: "app-new-bill",
  templateUrl: "./new-bill.component.html",
  styleUrls: ["./new-bill.component.scss"],
})
export class EditBillComponent implements OnInit {
  billFormGroup!: FormGroup;
  message: string = "";
  billID!: number;
  constructor(
    private fb: FormBuilder,
    private billService: BillService,
    private router: ActivatedRoute
  ) {}
  ngOnInit(): void {
    this.billID = this.router.snapshot.params["id"];
    this.billService.getBillById(this.billID).subscribe({
      next: (bill) => {
        this.billFormGroup = this.fb.group({
          id: this.fb.control(this.billID),
          billingDate: this.fb.control(bill.billingDate, [Validators.required]),
        });
      },
      error: (err) => alert(err.error),
    });
  }
  getErrorMessage(name: string, error: ValidationErrors): string {
    if (error["required"]) return `${name}  is required`;
    if (error["minlength"])
      return `The minimum length of ${name} is ${error["minlength"]["requiredLength"]}`;
    if (error["min"])
      return `The min value of ${name} is ${error["min"]["min"]}`;
    return "";
  }
  handleEditBillFormSubmit() {
    const { id, name, email } = this.billFormGroup.value;
    this.billService.editBill(id, name, email).subscribe({
      next: (bill) => {
        this.billFormGroup.reset();
        this.message = "Bill edited successfully";
      },
      error: (err) => console.log(err),
    });
  }
  handleCloseAlert() {
    this.message = "";
  }
}
<div class="container mt-3 col-md-6 offset-md-3">
  <div
    *ngIf="message.length > 0"
    class="alert alert-success alert-dismissible fade show"
    role="alert"
  >
    <button type="button" class="close" data-dismiss="alert" aria-label="Close">
      <span aria-hidden="true">&times;</span>
      <span class="sr-only" (click)="handleCloseAlert()">Close</span>
    </button>
    <strong>
      {{ message }}
    </strong>
  </div>
  <form
    *ngIf="billFormGroup"
    [formGroup]="billFormGroup"
    (ngSubmit)="handleEditBillFormSubmit()"
  >
    <div class="mb-3">
      <label class="form-label">Bill ID</label>
      <input type="text" formControlName="id" class="form-control" readonly />
    </div>

    <div class="mb-3">
      <label class="form-label">Bill name</label>
      <input type="text" formControlName="name" class="form-control" />
      <small
        class="text-danger"
        *ngIf="
          billFormGroup.controls['name'].errors &&
          billFormGroup.controls['name'].touched
        "
      >
        {{ getErrorMessage("Name", billFormGroup.controls["name"].errors) }}
      </small>
    </div>

    <div class="mb-3">
      <label class="form-label">Bill price</label>
      <input type="text" formControlName="price" class="form-control" />
      <small
        class="text-danger"
        *ngIf="
          billFormGroup.controls['price'].errors &&
          billFormGroup.controls['price'].touched
        "
      >
        {{ getErrorMessage("Price", billFormGroup.controls["price"].errors) }}
      </small>
    </div>

    <div class="mb-3">
      <label class="form-label">Promoted</label>
      <input type="checkbox" formControlName="promotion" class="form-check" />
      <small
        class="text-danger"
        *ngIf="
          billFormGroup.controls['promotion'].errors &&
          billFormGroup.controls['promotion'].touched
        "
      >
        {{
          getErrorMessage(
            "Promotion",
            billFormGroup.controls["promotion"].errors
          )
        }}
      </small>
    </div>
    <div class="mb-3">
      <button
        type="submit"
        class="btn btn-outline-warning"
        [disabled]="!billFormGroup.valid"
      >
        Edit bill
      </button>
    </div>
  </form>
</div>
