import { ActivatedRoute } from "@angular/router";
import { Bill } from "./../model/bill.model";
import { OnInit } from "@angular/core";
import { Component } from "@angular/core";
import { BillService } from "../services/bill.service";

@Component({
  selector: "app-show-bill",
  templateUrl: "./show-bill.component.html",
  styleUrls: ["./show-bill.component.scss"],
})
export class ShowBillComponent implements OnInit {
  bill!: Bill;
  errorMessage!: string;
  ngOnInit(): void {}
  constructor(private route: ActivatedRoute, private billService: BillService) {
    this.billService
      .getBillById(Number(this.route.snapshot.params["id"]))
      .subscribe({
        next: (data) => {
          this.bill = data;
        },
        error: (error) => {
          this.errorMessage = error.message;
        },
      });
  }
}
<div class="container">
  <div class="row">
    <div class="col-md-1"></div>
    <div class="col-md-1">
      <h1>Bill {{ bill.id }}</h1>
    </div>
    <div class="col-md-1">
      <h1>Ordered at {{ bill.billingDate }}</h1>
    </div>
  </div>
  <div class="row justify-content-center align-items-center">
    <div class="col-md-6">Customer</div>
  </div>
  <div class="row justify-content-center align-items-center">
    <div class="col-md-10">
      <div class="row">
        <div class="col-md-2">Customer name</div>
        <div class="col-md-1"></div>
        <div class="col-md-9">{{ bill.customer.name }}</div>
      </div>
      <div class="row">
        <div class="col-md-2">Customer email</div>
        <div class="col-md-1"></div>
        <div class="col-md-9">{{ bill.customer.email }}</div>
      </div>
    </div>
  </div>
  <div class="row justify-content-center align-items-center">
    <div class="col-md-10">
      <div class="row">
        <div class="col-md-2">Product items</div>
        <div class="col-md-1"></div>
        <div class="col-md-9">{{ bill.customer.name }}</div>
      </div>
      <div class="row">
        <div class="col-md-2">Customer email</div>
        <div class="col-md-1"></div>
        <div class="col-md-9">{{ bill.customer.email }}</div>
      </div>
    </div>
  </div>
</div>
