import { Product, PageProduct } from "./../model/product.model";
import { Injectable, OnInit } from "@angular/core";
import { Observable, of, throwError } from "rxjs";
import { UUID } from "angular2-uuid";
import { HttpClient } from "@angular/common/http";

@Injectable({
  providedIn: "root",
})
export class ProductService implements OnInit {
  private products!: Array<Product>;
  private BaseURI = "http://localhost:8888/INVENTORY-SERVICE/products";
  constructor(private httpClient: HttpClient) {
    this.products = new Array<Product>();
    for (let index = 0; index < 101; index++) {
      this.products.push({
        id: UUID.UUID(),
        name: `Product ${Number(index) + 1}`,
        price: Math.round(Math.random() * 100) * 1000,
        promotion: Math.random() > 0.4,
      });
    }
  }
  ngOnInit(): void {}
  public getAllProducts(): Observable<Array<Product>> {
    this.httpClient.get(this.BaseURI).subscribe({
      next: (data) => {
        console.log(data);
        alert("get products done");
      },
      error: (err) => console.table(err),
    });
    return of(this.products);
  }
  public getPageProduct(page: number, size: number): Observable<PageProduct> {
    let index = page * size;
    let totalPages =
      ~~(this.products.length / size) +
      (this.products.length % size > 0 ? 1 : 0);
    let prods = this.products.slice(index, index + size);
    console.log({
      products: prods,
      page: page,
      size: size,
      totalPages: totalPages,
    });
    return of({
      products: prods,
      page: page,
      size: size,
      totalPages: totalPages,
    });
  }
  public deleteProduct(productID: string): Observable<boolean> {
    this.products = this.products.filter((p) => p.id != productID);
    return of(true);
  }

  public setProductPromotion(productID: string): Observable<boolean> {
    let product = this.products.find((p) => p.id == productID);
    if (product) {
      product.promotion = !product.promotion;
      return of(true);
    }
    return throwError(() => new Error("Product not found"));
  }
  public getProductsByKeyword(keyword: string): Observable<PageProduct> {
    return this.httpClient.get<PageProduct>(`${this.BaseURI}`);
  }
  public getPageProductsByKeyword(
    keyword: string,
    page: number,
    size: number
  ): Observable<PageProduct> {
    return this.httpClient.get<PageProduct>(
      `${this.BaseURI}?keyword=${keyword}&page=${page}&`
    );
  }
  public addProduct(
    name: string,
    price: number,
    promotion: boolean
  ): Observable<Product> {
    const uuid = UUID.UUID();
    this.products.push({
      id: uuid,
      name: name,
      price: price,
      promotion: promotion,
    });
    return this.getProductById(uuid);
  }
  public editProduct(
    id: string,
    name: string,
    price: number,
    promotion: boolean
  ): Observable<Product> {
    // this.products.reduce({
    //   id: uuid,
    //   name: name,
    //   price: price,
    //   promotion: promotion,
    // });
    this.products = this.products.map((p) =>
      p.id == id
        ? { id: id, name: name, price: price, promotion: promotion }
        : p
    );
    return this.getProductById(id);
  }
  public getProductById(id: string): Observable<Product> {
    const product = this.products.find((p) => p.id == id);
    if (product == undefined)
      return throwError(() => new Error("Product not found"));
    return of(product);
  }
}
