import { Component, OnInit } from "@angular/core";
import { SmoothieChart, TimeSeries } from "smoothie";
import { SecurityService } from "../services/security.service";

@Component({
  selector: "app-analytics",
  templateUrl: "./analytics.component.html",
  styleUrls: ["./analytics.component.scss"],
})
export class AnalyticsComponent implements OnInit {
  constructor(public authService: SecurityService) {}
  series!: TimeSeries;

  canvas!: HTMLElement | null;

  chart = new SmoothieChart();
  ngOnInit(): void {
    this.series = new TimeSeries();
    this.canvas = document.getElementById("chart");
    this.chart.addTimeSeries(this.series, {
      strokeStyle: "rgba(0, 255, 0, 1)",
    });
    if (this.canvas) this.chart.streamTo(this.canvas, 500);
  }
}
<div class="container">
  <div class="row">
    <div class="col-6 bg-success">Hello</div>
    <div class="col-6 bg-success">Everyone</div>
  </div>
  <div class="row">
    <canvas id="chart" class="col-8 mx-auto"></canvas>
  </div>
</div>
